
public class ComputeArea {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//declare variables
		double PI;
		double radius;
		double area;
		
		//initialize values
		PI = 3.14;
		radius = 3;
		area = PI * Math.pow(radius, 2);
		
		// output the area
		System.out.println("Print the area here...");
		System.out.println(area);
		
		
	}

}
